import React, {useEffect, useState} from 'react';
import '../../App.css';
import Recipe from '../../recipe';
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux'
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';



const Dashboard = (props) => {




  const APP_ID = 'a99bd604';
  const APP_KEY = '801b3f6953e413a5d229de1043ba6dbd';
  
  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('');
  
  
  

  useEffect(() => {
    const getRecipes = async () => {
      const response = await fetch(`https://api.edamam.com/search?q=${query}&app_id=${APP_ID}&app_key=${APP_KEY}`);
      const data = await response.json();
      setRecipes(data.hits);
      console.log(data);
    }
    getRecipes();
  }, [query]);

  // const getRecipes = async () => {
  //   const response = await fetch(`https://api.edamam.com/search?q=${query}&app_id=${APP_ID}&app_key=${APP_KEY}`);
  //   const data = await response.json();
  //   setRecipes(data.hits);
  // }

  const updateSearch = e => {
    setSearch(e.target.value);
  }

  const getSearch = e => {
    e.preventDefault();
    setQuery(search);
    setSearch('');
  }
 
  const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
        width: '25ch',
      },
    },
  }));
  
  const classes = useStyles();
  
  const { auth } = props;
  if (!auth.uid) return <Redirect to='/signin' /> 


  

  return (
    <div className="App">
      <h1 className="app-title">EasyMeal</h1>
      <p className="app-subtitle">Easy search for recipes.</p>
      <form onSubmit={getSearch} className={classes.root} noValidate autoComplete="off">
      <TextField id="filled-basic" label="Filled" variant="filled" justify="center"/>
        <input className="search-bar" type="text" value={search} onChange={updateSearch} placeholder="Enter an ingredient or a dish name" ></input>
        <button className="search-button" type="submit">
          Search
        </button>
      </form>
      <div className="reciperesults">
      {recipes.map(recipe => (
        <Recipe 
        key={recipe.recipe.label}
        url={recipe.recipe.url}
        title={recipe.recipe.label} 
        calories={recipe.recipe.calories}
        image={recipe.recipe.image}
        ingredients={recipe.recipe.ingredients}></Recipe>
      ))}
      </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  // console.log(state);
  return {
    //projects: state.firestore.ordered.projects,
    auth: state.firebase.auth,
    //notifications: state.firestore.ordered.notifications
  }
}

export default connect(mapStateToProps)(Dashboard);
