import React, { Component } from 'react'
import { connect } from 'react-redux'
import { signIn } from '../../store/actions/authActions'
import { Redirect } from 'react-router-dom'

class SignIn extends Component {
  state = {
    email: '',
    password: ''
  }
  handleChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value
    })
  }
  handleSubmit = (e) => {
    e.preventDefault();
    this.props.signIn(this.state)
  }
  render() {
    const { authError, auth } = this.props;
    if (auth.uid) return <Redirect to='/' /> 
    return (
        <Grid container 
        spacing={0} 
        direction="row"
        alignItems="center"
        justify="center"
        style={{ minHeight: '80vh' }}>
            <Grid item xs={10} sm={6} md={6} lg={4} xl={4} >
            <Grow in={true} timeout={1000}> 
                <Card>
                    <CardHeader 
                        title="User Login"
                        avatar={
                            <Avatar>
                                <Person style={{fontSize:30}} />
                            </Avatar>
                        }
                    />
                    
                    <CardContent>
                        <Toast message={state.errorMessage} status={state.showToast} showToast={showToast} />
                        <form onSubmit={handleSubmit} autoComplete="off">
                            <FormControl margin="normal" required fullWidth>
                                <InputLabel htmlFor="email">Username or Email</InputLabel>
                                <Input id="email" onChange={handleChange} type="text" autoFocus />
                            </FormControl>

                            <FormControl fullWidth required>
                                <InputLabel htmlFor="password">Password</InputLabel>
                                <Input
                                id="password"
                                type={values.showPassword ? 'text' : 'password'}
                                value={values.password}
                                onChange={handleChange}
                                endAdornment={
                                    <InputAdornment position="end">
                                    <IconButton aria-label="Toggle password visibility" onClick={handleClickShowPassword}>
                                        {values.showPassword ? <Visibility /> : <VisibilityOff />}
                                    </IconButton>
                                    </InputAdornment>
                                }
                                />
                            </FormControl>

                            <FormControlLabel
                                control={<Checkbox value="remember" color="primary" />}
                                label="Remember me"
                            />

                        
                        <Button 
                            fullWidth 
                            variant="contained" 
                            type="submit"
                            color="primary"
                            style={{marginTop:20}}>Login</Button>
                        
                        

                            {state.showProgress && <LinearProgress />}
                        </form>
                    </CardContent>
                </Card>
                    </Grow>
            </Grid>
        </Grid>
    )
  }
}

const mapStateToProps = (state) => {
  return{
    authError: state.auth.authError,
    auth: state.firebase.auth
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    signIn: (creds) => dispatch(signIn(creds))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(SignIn)
